SELECT iibs.id as id, iibs.internship_type as type, s.student_name as studentName, iibsl.`status` as status, ipi.plan_title as planTitle, iibs.reason as reason,
iibs.company_name as companyName, iibs.job_department as jobDepartment, iibs.job_name as jobName,
iibs.company_name as companyName, iibs.job_category as jobCategory, iibs.job_name as jobName,
iibs.create_time as createTime, iibsl.create_time as checkTime, iibsl.fail_reason as failReason FROM internship_info_by_student_log iibsl LEFT JOIN internship_info_by_student iibs ON iibsl.relation_internship_info_by_student_id = iibs.id and iibs.deleted=0 LEFT JOIN internship_plan_info ipi ON iibs.relation_plan_id = ipi.id and ipi.deleted=0 LEFT JOIN student_info s on s.id=iibs.relation_student_id and s.deleted=0 LEFT JOIN internship_with_teacher iwt on iwt.relation_plan_id = iibs.relation_plan_id and iwt.deleted = 0 LEFT JOIN teacher_info t on t.id = iwt.teacher_id and t.deleted = 0 LEFT JOIN major_info mi ON mi.id = ipi.major_id AND mi.deleted = 0
iibsl.deleted=0
AND iibs.relation_plan_id = #{planId}
AND iibs.internship_type = #{internshipType}
AND iibsl.check_status = #{checkStatus}
AND s.name like CONCAT('%',#{key},'%')
AND iibs.status in (2,3)
AND iibsl.status = 2
AND iibsl.status = 3
and ipi.id = #{planId}
and t.id = #{teacherId}
and mi.id = #{majorId}
order by iibsl.create_time desc limit #{start},#{size}

