iibsl.deleted=0
AND iibs.relation_plan_id = #{planId}
AND iibs.internship_type = #{internshipType}
AND iibsl.check_status = #{checkStatus}
AND s.name like CONCAT('%',#{key},'%')
AND iibs.status in (2,3)
AND iibsl.status = 2
AND iibsl.status = 3
and ipi.id = #{planId}
and t.id = #{teacherId}

SELECT count(*) FROM internship_info_by_student iibs LEFT JOIN internship_plan_info ipi ON iibs.relation_plan_id = ipi.id AND ipi.deleted = 0 LEFT JOIN student_info s on s.user_id = iibs.create_by and s.deleted = 0 LEFT JOIN internship_with_teacher iwt on iwt.relation_plan_id = iibs.relation_plan_id and iwt.deleted = 0 LEFT JOIN teacher_info t on t.id = iwt.teacher_id and t.deleted = 0
iibs.deleted =0
AND iibs.relation_plan_id = #{planId}
AND iibs.internship_type = #{internshipType}
AND iibs.check_status = #{checkStatus}
AND s.name like CONCAT('%',#{key},'%')
AND iibs.status = 1
and ipi.id = #{planId}
and t.id = #{teacherId}

SELECT iibs.id as id, iibs.internship_type as type, s.student_name as studentName, ipi.plan_title as planTitle,
iibs.company_name as companyName, iibs.job_name as jobName, iibs.job_department as jobDepartment,
iibs.reason as reason,
iibs.company_name as companyName, iibs.job_name as jobName, iibs.job_category as jobCategory,
iibs.create_time as createTime, iibs.`status` as status FROM internship_info_by_student iibs LEFT JOIN internship_plan_info ipi ON iibs.relation_plan_id = ipi.id AND ipi.deleted = 0 LEFT JOIN student_info s on s.user_id = iibs.create_by and s.deleted = 0 LEFT JOIN internship_with_teacher iwt on iwt.relation_plan_id = iibs.relation_plan_id and iwt.deleted = 0 LEFT JOIN teacher_info t on t.id = iwt.teacher_id and t.deleted = 0 LEFT JOIN major_info mi ON mi.id = ipi.major_id AND mi.deleted = 0
iibs.deleted =0
AND iibs.relation_plan_id = #{planId}
AND iibs.internship_type = #{internshipType}
AND iibs.check_status = #{checkStatus}
AND s.name like CONCAT('%',#{key},'%')
AND iibs.status = 1
and ipi.id = #{planId}
and t.id = #{teacherId}
and mi.id = #{majorId}

SELECT u.user_avatar as userAvatar, iibs.relation_student_id as id, u.user_name as studentName, uc.account_number as studentNumber, iibs.company_name as companyName, iibs.job_name as jobName FROM internship_info_by_student iibs left join `user` u on iibs.create_by = u.id and u.deleted = 0 left join user_account uc on iibs.create_by = uc.user_id and uc.deleted = 0 WHERE iibs.deleted = 0
AND iibs.relation_plan_id = #{planId}
AND iibs.internship_type = 1 and iibs.relation_student_id in(SELECT iws.relation_student_id FROM internship_with_student iws WHERE iws.deleted = 0
AND iibs.relation_plan_id = #{planId}
AND iws.relation_teacher_id = #{teacherId}
) limit #{start},#{size}

