SELECT u.user_avatar as userAvatar, iws.relation_student_id as id, u.user_name as studentName, uc.account_number as studentNumber FROM internship_with_student iws left join student_info si on iws.relation_student_id = si.id and si.deleted = 0 left join `user` u on si.user_id = u.id and u.deleted = 0 left join user_account uc on si.user_id = uc.user_id and uc.deleted = 0 WHERE iws.deleted = 0
AND iws.relation_plan_id = #{planId}
AND iws.relation_teacher_id = #{teacherId}
AND iws.relation_student_id NOT IN ( SELECT iibs.relation_student_id FROM internship_info_by_student iibs LEFT JOIN `user` u ON iibs.create_by = u.id AND u.deleted = 0 LEFT JOIN user_account uc ON iibs.create_by = uc.user_id AND uc.deleted = 0 WHERE iibs.deleted = 0
AND iibs.relation_plan_id = #{planId}
AND iibs.internship_type = 1 AND iibs.relation_student_id IN ( SELECT iws.relation_student_id FROM internship_with_student iws WHERE iws.deleted = 0
AND iws.relation_plan_id = #{planId}
AND iws.relation_teacher_id = #{teacherId} ) ) limit #{start},#{size}

