mapper.select:
SELECT ipi.id as id, ipi.plan_title as planTitle, mi.major_name as majorName, gi.grade_year as gradeYear, ipi.gradation as gradation, ipi.start_time as startTime, ipi.end_time as endTime, ipi.`status` as status FROM internship_plan_info ipi LEFT JOIN major_info mi ON ipi.major_id = mi.id AND mi.deleted = 0 LEFT JOIN grade_info gi ON gi.id = ipi.grade_id AND gi.deleted = 0 WHERE ipi.deleted = 0
AND ipi.school_id = #{schoolId}
AND ipi.department_id = #{departmentId}
AND ipi.major_id = #{majorId}
AND ipi.grade_id = #{gradeId}
AND ipi.plan_title like concat('%',#{key},'%')
AND ipi.status = #{status}
order by ipi.create_time LIMIT #{start},#{size}

SELECT a.id as id, a.plan_title as planTitle, a.grade_id as gradeId, a.grade_year as gradeYear, a.major_id as majorId, a.major_name as majorName, a.start_time as startTime, a.end_time as endTime, a.department_id as departmentId, a.school_id as schoolId, GROUP_CONCAT( teacher_name SEPARATOR ',' ) AS teacherNames FROM ( SELECT ipi.id, ipi.plan_title, ipi.grade_id, gi.grade_year, ipi.major_id, mi.major_name, ipi.start_time, ipi.end_time, iwt.teacher_id, ti.teacher_name, ipi.create_time, ipi.school_id, ipi.department_id FROM internship_plan_info ipi LEFT JOIN internship_with_teacher iwt ON iwt.relation_plan_id = ipi.id AND iwt.deleted = 0 LEFT JOIN major_info mi ON mi.id = ipi.major_id AND mi.deleted = 0 LEFT JOIN grade_info gi ON gi.id = ipi.grade_id AND gi.deleted = 0 LEFT JOIN teacher_info ti ON ti.id = iwt.teacher_id AND ti.deleted = 0 where ipi.deleted = 0 ) a
AND a.school_id = #{schoolId}
AND a.department_id = #{departmentId}
AND a.major_id = #{majorId}
AND a.grade_id = #{gradeId}
AND a.plan_title like concat('%',#{key},'%')
AND ( SELECT count(*) FROM internship_with_teacher WHERE relation_plan_id = a.id and internship_with_teacher.deleted=0 ) = 0
AND ( SELECT count(*) FROM internship_with_teacher WHERE relation_plan_id = a.id and internship_with_teacher.deleted=0 ) > 0
GROUP BY id order by a.create_time LIMIT #{start},#{size}

SELECT count(*) FROM ( SELECT ipi.id, ipi.plan_title, ipi.grade_id, ipi.major_id, iwt.teacher_id, ipi.school_id, ipi.department_id FROM internship_plan_info ipi LEFT JOIN internship_with_teacher iwt ON iwt.relation_plan_id = ipi.id AND iwt.deleted = 0 LEFT JOIN major_info mi ON mi.id = ipi.major_id AND mi.deleted = 0 LEFT JOIN grade_info gi ON gi.id = ipi.grade_id AND gi.deleted = 0 LEFT JOIN teacher_info ti ON ti.id = iwt.teacher_id AND ti.deleted = 0 where ipi.deleted = 0 GROUP BY ipi.id ) a
AND a.school_id = #{schoolId}
AND a.department_id = #{departmentId}
AND a.major_id = #{majorId}
AND a.grade_id = #{gradeId}
AND a.plan_title like concat('%',#{key},'%')
AND ( SELECT count(*) FROM internship_with_teacher WHERE relation_plan_id = a.id and internship_with_teacher.deleted=0 ) = 0
AND ( SELECT count(*) FROM internship_with_teacher WHERE relation_plan_id = a.id and internship_with_teacher.deleted=0 ) > 0

SELECT
count(*)
FROM
internship_plan_info ipi
LEFT JOIN major_info mi ON ipi.major_id = mi.id
AND mi.deleted = 0
LEFT JOIN grade_info gi ON gi.id = ipi.grade_id
AND gi.deleted = 0
WHERE
ipi.deleted = 0

	AND ipi.school_id = #{schoolId}


	AND ipi.department_id = #{departmentId}


	AND ipi.major_id = #{majorId}


	AND ipi.grade_id = #{gradeId}


	AND ipi.plan_title like concat('%',#{key},'%')


	AND ipi.status = #{status}

SELECT iwt.teacher_id AS teacherId, IFNULL(GROUP_CONCAT( iws.relation_student_id SEPARATOR ',' ) , '') AS studentIds, ti.teacher_name AS teacherName, IFNULL(GROUP_CONCAT( si.student_name SEPARATOR ',' ),'') AS studentNames FROM internship_with_teacher iwt LEFT JOIN internship_with_student iws ON iwt.relation_plan_id = iws.relation_plan_id AND iwt.teacher_id = iws.relation_teacher_id AND iws.deleted = 0 LEFT JOIN teacher_info ti ON ti.id = iwt.teacher_id AND ti.deleted = 0 LEFT JOIN student_info si ON si.id = iws.relation_student_id AND si.deleted = 0 WHERE iwt.deleted = 0
AND iwt.relation_plan_id = #{planId}
GROUP BY iwt.teacher_id
