mapper.select:
SELECT si.student_name AS studentName, iibs.company_name AS companyName, iibs.job_name AS jobName, p.student_id AS studentId, p.date_time AS dataTime, p.create_time AS createTime FROM `internship_info_by_student` AS iibs LEFT JOIN `internship_with_teacher` iwt on iibs.relation_plan_id=iwt.relation_plan_id LEFT JOIN `student_info` si on iibs.relation_student_id=si.id LEFT JOIN `payroll` p on iibs.relation_student_id = p.student_id LEFT JOIN `internship_plan_info` ipi on iibs.relation_plan_id=ipi.id WHERE p.deleted=0 AND iibs.deleted=0 AND ipi.deleted=0
AND ipi.major_id=#{majorId}
AND p.create_time<#{createTime}
AND si.name like concat ('%',#{studentName},'%')
AND iwt.teacher_id =#{teacherId}

SELECT gi.grade_year AS gradeYear, mj.id AS majorId, mj.major_name AS majorName, ci.class_name AS className, si.student_number AS studentNumber, si.student_name AS studentName, si.id AS studentId, iibs.company_name AS companyName, iibs.job_name AS jobName, p.salary AS salary, p.date_time as dateTime, p.deleted FROM `payroll` AS p LEFT JOIN `internship_info_by_student` AS iibs on iibs.relation_student_id=p.student_id LEFT JOIN `student_info` AS si on si.id=p.student_id LEFT JOIN internship_plan_info AS ipi on ipi.id=p.internship_id LEFT JOIN school_teacher_relation AS str on str.school_id=ipi.school_id LEFT JOIN `major_info` AS mj on mj.id=ipi.major_id LEFT JOIN `class_teacher_relation` AS ctr on ctr.teacher_id=str.teacher_id LEFT JOIN `class_info` AS ci on ci.id= ctr.class_id LEFT JOIN `grade_info` AS gi on gi.school_id=str.school_id WHERE p.deleted=0
AND si.student_name like concat ('%',#{studentName},'%')
AND gi.grade_year=#{gradeYear}
AND mj.major_name=#{majorName}
AND p.date_time>#{dateTime}
ORDER BY gi.grade_year,ci.class_name,p.create_time DESC
limit #{page},#{size}
