SELECT u.user_avatar AS userAvatar, u.user_name AS userName, rws.id AS id, rws.create_time AS createTime, rws.title AS title, rws.content, rws.`status` AS status, rws.img_url AS imgUrl, rws.url AS url, rws.score AS score, rws.evaluation AS evaluation, rws.report_type AS reportType, rws.reason AS reason FROM report_with_student AS rws LEFT JOIN student_info AS si on rws.student_id=si.id LEFT JOIN `user` AS u on si.user_id=u.id WHERE u.`status`=0 AND u.deleted=0 AND rws.deleted=0 AND rws.report_type=1
AND rws.student_id=#{studentId}
AND rws.id=#{id}

SELECT u.user_avatar AS userAvatar, u.user_name AS userName, mi.id AS majorId, rws.id, rws.content, rws.student_id AS studentId, rws.create_time AS createTime, rws.report_type AS report_type, rws.title, rws.`status`, rws.reason FROM report_with_student AS rws LEFT JOIN internship_with_teacher AS iwt on rws.relation_plan_id=iwt.relation_plan_id LEFT JOIN teacher_info AS ti on iwt.teacher_id=ti.id LEFT JOIN student_info AS si on si.id=rws.student_id LEFT JOIN `user` AS u on u.id= si.user_id LEFT JOIN internship_plan_info AS ipi on rws.relation_plan_id=ipi.id LEFT JOIN major_info AS mi on ipi.major_id=mi.id WHERE u.`status`=0 AND u.deleted=0 AND rws.deleted=0 AND rws.report_type=1 AND rws.`status`in(1,2,3,4)
AND iwt.teacher_id=#{teacherId}
AND u.user_name like concat ('%',#{userName},'%')
AND mi.id=#{majorId}
AND rws.`status`=#{status}

SELECT DISTINCT iibs.relation_student_id AS studentId, u.user_name AS userName, u.user_avatar AS userAvatar, si.student_number AS studentNumber, gi.grade_year AS gradeYear, mi.major_name AS majorName, mi.id AS majorId FROM internship_info_by_student AS iibs LEFT JOIN internship_plan_info AS ipi ON iibs.relation_plan_id=ipi.id LEFT JOIN internship_with_teacher AS iwt ON ipi.id=iwt.relation_plan_id LEFT JOIN major_info AS mi ON ipi.major_id=mi.id LEFT JOIN grade_info AS gi ON ipi.school_id=gi.school_id LEFT JOIN report_with_student AS rws ON ipi.id=rws.relation_plan_id LEFT JOIN student_info AS si ON rws.student_id=si.id LEFT JOIN `user` AS u ON si.user_id=u.id WHERE iibs.relation_student_id NOT IN(SELECT DISTINCt student_id FROM report_with_student) AND rws.report_type in(1,2,3,4) AND rws.deleted=0
AND u.user_name like ('%',#{userName},'%')
AND mi.id=#{majorId}
AND iwt.teacher_id=#{teacherId}

SELECT DISTINCT iibs.relation_student_id AS studentId, u.user_name AS userName, u.user_avatar AS userAvatar, rws.create_time AS createTime, rws.report_type AS rws.reportType, rws.title AS title, rws.content AS content, rws.`status` AS status, mi.id AS majorId FROM internship_info_by_student AS iibs LEFT JOIN internship_plan_info AS ipi ON iibs.relation_plan_id=ipi.id LEFT JOIN internship_with_teacher AS iwt ON ipi.id=iwt.relation_plan_id LEFT JOIN major_info AS mi ON ipi.major_id=mi.id LEFT JOIN grade_info AS gi ON ipi.school_id=gi.school_id LEFT JOIN report_with_student AS rws ON ipi.id=rws.relation_plan_id LEFT JOIN student_info AS si ON rws.student_id=si.id LEFT JOIN `user` AS u ON si.user_id=u.id WHERE iibs.relation_student_id IN(SELECT DISTINCt student_id FROM report_with_student) AND rws.deleted=0 AND rws.`status`IN(1,2,3,4) AND rws.report_type in(1,2,3,4)
AND u.user_name like ('%',#{userName},'%')
AND mi.id=#{majorId}
AND iwt.teacher_id=#{teacherId}
AND rws.create_time>#{createTime}

SELECT u.user_avatar AS userAvatar, u.user_name AS userName, mi.id AS majorId, rws.id, rws.student_id AS studentId, rws.create_time AS createTime, rws.start_time AS startTime, rws.end_time AS endTime, rws.report_type AS reportType, rws.title, rws.`status`, rws.content, rws.reason FROM report_with_student AS rws LEFT JOIN internship_with_teacher AS iwt on rws.relation_plan_id=iwt.relation_plan_id LEFT JOIN teacher_info AS ti on iwt.teacher_id=ti.id LEFT JOIN student_info AS si on si.id=rws.student_id LEFT JOIN `user` AS u on u.id= si.user_id LEFT JOIN internship_plan_info AS ipi on rws.relation_plan_id=ipi.id LEFT JOIN major_info AS mi on ipi.major_id=mi.id WHERE u.`status`=0 AND u.deleted=0 AND rws.deleted=0 AND rws.report_type=2 AND rws.`status`in(1,2,3,4)
AND iwt.teacher_id=#{teacherId}
AND u.user_name like concat ('%',#{userName},'%')
AND mi.id=#{majorId}
AND rws.`status`=#{status}

