mapper.select:
SELECT , sai.id applyId, sai.`status` applyStatus FROM sign_info_by_student AS sibs LEFT JOIN sign_apply_info AS sai ON sibs.id = sai.sign_id AND sai.deleted = 0
sibs.internship_plan_id = #{planId}
AND sibs.deleted = 0 AND sibs.student_id = #{studentId} AND sibs.sign_date = #{date}
order by sibs.is_morning asc ,sibs.sign_type asc

SELECT , sai.id applyId, sai.`status` applyStatus FROM sign_info_by_student AS sibs LEFT JOIN sign_apply_info AS sai ON sibs.id = sai.sign_id AND sai.deleted = 0
sibs.internship_plan_id = #{planId}
AND sibs.deleted = 0 AND sibs.student_id = #{studentId} AND sibs.sign_date BETWEEN #{startTime} AND #{endTime}
ORDER BY sibs.sign_date ASC,sibs.is_morning ASC ,sibs.sign_type ASC

SELECT , sai.id applyId, sai.`status` applyStatus FROM sign_info_by_student AS sibs LEFT JOIN sign_apply_info AS sai ON sibs.id = sai.sign_id AND sai.deleted = 0 LEFT JOIN student_info AS s on sibs.student_id = s.id
sibs.internship_plan_id = #{planId}
AND sibs.deleted = 0
AND sibs.student_id = #{studentId}
AND sibs.sign_date = #{date}
AND s.class_id = #{classId}
and s.student_name like concat('%',#{key},'%')
AND sibs.sign_status is not null
ORDER BY sibs.sign_date Desc,sibs.is_morning Desc ,sibs.sign_type Desc limit #{start},#{size}

SELECT
    count(1)
FROM
sign_info_by_student AS sibs
LEFT JOIN sign_apply_info AS sai ON sibs.id = sai.sign_id AND sai.deleted = 0


        sibs.internship_plan_id = #{planId}

